from django.contrib import admin

from schedule.models import Calendar, Event, CalendarRelation, Rule

from uclabs import admin as uc_admin

class CalendarAdminOptions(admin.ModelAdmin):
    prepopulated_fields = {"slug": ("name",)}
    search_fields = ['name']


uc_admin.custom_admin.register(Calendar, CalendarAdminOptions)
uc_admin.custom_admin.register([Rule, Event, CalendarRelation])
